import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  TextField,
  Button,
  Card,
  CardMedia,
  CardContent,
  CardActions,
} from '@material-ui/core';
import axios from 'axios';

// Default medicine image if specific one is not found
const defaultMedicineImage = 'https://via.placeholder.com/200x200?text=Medicine';

function Inventory() {
  const [inventory, setInventory] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/inventory');
        setInventory(response.data);
      } catch (err) {
        setError('Failed to fetch inventory data');
      }
    };

    fetchInventory();
  }, []);

  return (
    <Container>
      <Box mt={4}>
        <Typography variant="h4" gutterBottom>
          Available Medicines
        </Typography>
        {error ? (
          <Typography color="error">{error}</Typography>
        ) : (
          <Grid container spacing={3}>
            {inventory.map((item) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={item.pid}>
                <Card>
                  <CardMedia
                    component="img"
                    height="200"
                    image={defaultMedicineImage}
                    alt={item.pname}
                    style={{ objectFit: 'contain', padding: '16px' }}
                  />
                  <CardContent>
                    <Typography variant="h6" component="h2">
                      {item.pname}
                    </Typography>
                    <Typography color="textSecondary">
                      Manufacturer: {item.manufacturer}
                    </Typography>
                    <Typography color="textSecondary">
                      Stock: {item.quantity}
                    </Typography>
                    <Typography variant="h6" color="primary">
                      ${item.price}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <TextField
                      type="number"
                      label="Quantity"
                      size="small"
                      InputProps={{ inputProps: { min: 1, max: item.quantity } }}
                    />
                    <Button variant="contained" color="primary">
                      Add to Cart
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
            {inventory.length === 0 && (
              <Grid item xs={12}>
                <Paper style={{ padding: '20px', textAlign: 'center' }}>
                  <Typography>No inventory items found</Typography>
                </Paper>
              </Grid>
            )}
          </Grid>
        )}
      </Box>
    </Container>
  );
}

export default Inventory;
