import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Paper,
  Typography,
  Box,
  TextField,
  Button,
  Card,
  CardMedia,
  CardContent,
  CardActions,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Snackbar,
  Alert
} from '@mui/material';
import axios from 'axios';

// Default medicine image if specific one is not found
const defaultMedicineImage = 'https://via.placeholder.com/200x200?text=Medicine';

function Inventory() {
  const [inventory, setInventory] = useState([]);
  const [error, setError] = useState('');
  const [orderQuantities, setOrderQuantities] = useState({});
  const [openOrderDialog, setOpenOrderDialog] = useState(false);
  const [orderByPid, setOrderByPid] = useState('');
  const [orderQuantity, setOrderQuantity] = useState(1);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'info' });
  const [searchTerm, setSearchTerm] = useState('');

  const user = JSON.parse(localStorage.getItem('user')); // Get logged in user

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/inventory');
      setInventory(response.data);
    } catch (err) {
      setError('Failed to fetch inventory data');
    }
  };

  const handleQuantityChange = (pid, value) => {
    setOrderQuantities({ ...orderQuantities, [pid]: value });
  };

  const placeOrder = async (pid, quantity, sid) => {
    try {
      const response = await axios.post('http://localhost:5000/api/orders', {
        pid,
        uid: user.uid,
        sid,
        quantity: parseInt(quantity)
      });
      
      setSnackbar({
        open: true,
        message: 'Order placed successfully!',
        severity: 'success'
      });
      
      fetchInventory(); // Refresh inventory
      setOrderQuantities({}); // Reset quantities
    } catch (err) {
      setSnackbar({
        open: true,
        message: err.response?.data?.error || 'Failed to place order',
        severity: 'error'
      });
    }
  };

  const handleOrderByPid = async () => {
    const product = inventory.find(item => item.pid === orderByPid);
    if (!product) {
      setSnackbar({
        open: true,
        message: 'Product ID not found',
        severity: 'error'
      });
      return;
    }
    
    await placeOrder(orderByPid, orderQuantity, product.sid);
    setOpenOrderDialog(false);
    setOrderByPid('');
    setOrderQuantity(1);
  };

  const getStockStatus = (quantity) => {
    if (quantity === 0) return { label: 'Out of Stock', color: 'error' };
    if (quantity <= 5) return { label: 'Low Stock', color: 'warning' };
    return { label: 'In Stock', color: 'success' };
  };

  const filteredInventory = inventory.filter(item =>
    item.pname.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.pid.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.manufacturer.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Container>
      <Box mt={4}>
        <Typography variant="h4" gutterBottom>
          Available Medicines
        </Typography>

        <Box mb={3}>
          <TextField
            fullWidth
            variant="outlined"
            label="Search by name, ID or manufacturer"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </Box>

        <Box mb={3}>
          <Button
            variant="contained"
            color="primary"
            onClick={() => setOpenOrderDialog(true)}
          >
            Order by Product ID
          </Button>
        </Box>

        {error ? (
          <Typography color="error">{error}</Typography>
        ) : (
          <Grid container spacing={3}>
            {filteredInventory.map((item) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={item.pid}>
                <Card>
                  <CardMedia
                    component="img"
                    height="200"
                    image={defaultMedicineImage}
                    alt={item.pname}
                    style={{ objectFit: 'contain', padding: '16px' }}
                  />
                  <CardContent>
                    <Typography variant="h6" component="h2">
                      {item.pname}
                    </Typography>
                    <Typography color="textSecondary" gutterBottom>
                      ID: {item.pid}
                    </Typography>
                    <Typography color="textSecondary">
                      Manufacturer: {item.manufacturer}
                    </Typography>
                    <Typography color="textSecondary">
                      Expiry: {new Date(item.exp).toLocaleDateString()}
                    </Typography>
                    <Box mt={1}>
                      <Chip 
                        label={getStockStatus(item.quantity).label}
                        color={getStockStatus(item.quantity).color}
                      />
                    </Box>
                    <Typography variant="h6" color="primary" style={{ marginTop: '8px' }}>
                      â‚¹{(item.price / 100).toFixed(2)}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <TextField
                      type="number"
                      label="Quantity"
                      size="small"
                      value={orderQuantities[item.pid] || ''}
                      onChange={(e) => handleQuantityChange(item.pid, e.target.value)}
                      InputProps={{ 
                        inputProps: { 
                          min: 1, 
                          max: item.quantity 
                        } 
                      }}
                      disabled={item.quantity === 0}
                    />
                    <Button 
                      variant="contained" 
                      color="primary"
                      disabled={item.quantity === 0 || !orderQuantities[item.pid]}
                      onClick={() => placeOrder(item.pid, orderQuantities[item.pid], item.sid)}
                    >
                      Order
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Box>

      {/* Order by Product ID Dialog */}
      <Dialog open={openOrderDialog} onClose={() => setOpenOrderDialog(false)}>
        <DialogTitle>Order by Product ID</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Product ID"
            fullWidth
            value={orderByPid}
            onChange={(e) => setOrderByPid(e.target.value.toUpperCase())}
          />
          <TextField
            margin="dense"
            label="Quantity"
            type="number"
            fullWidth
            value={orderQuantity}
            onChange={(e) => setOrderQuantity(e.target.value)}
            InputProps={{ inputProps: { min: 1 } }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenOrderDialog(false)} color="primary">
            Cancel
          </Button>
          <Button onClick={handleOrderByPid} color="primary" variant="contained">
            Place Order
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for notifications */}
      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={6000} 
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert onClose={() => setSnackbar({ ...snackbar, open: false })} severity={snackbar.severity}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default Inventory;
