import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Box,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  CircularProgress,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';
import axios from 'axios';

function Orders({ user }) {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [newOrder, setNewOrder] = useState({
    pid: '',
    sid: '',
    quantity: ''
  });
  const [products, setProducts] = useState([]);
  const [sellers, setSellers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedProductSellers, setSelectedProductSellers] = useState([]);
  const [cancellingOrder, setCancellingOrder] = useState(null);

  useEffect(() => {
    fetchOrders();
    fetchProducts();
  }, [user]);

  const fetchOrders = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/orders/${user.uid}`);
      setOrders(response.data);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError(err.response?.data?.error || 'Failed to fetch orders');
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
    }
  };

  const fetchProductSellers = async (pid) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/inventory/${pid}`);
      setSelectedProductSellers(response.data);
    } catch (err) {
      console.error('Error fetching sellers:', err);
      setSelectedProductSellers([]);
    }
  };

  const handleProductChange = (e) => {
    const pid = e.target.value;
    setError('');
    setNewOrder({ ...newOrder, pid, sid: '', quantity: '' });
    if (pid) {
      fetchProductSellers(pid);
    } else {
      setSelectedProductSellers([]);
    }
  };

  const handlePlaceOrder = async () => {
    try {
      setLoading(true);
      if (!newOrder.pid || !newOrder.sid || !newOrder.quantity) {
        setError('Please fill in all fields');
        return;
      }

      const response = await axios.post('http://localhost:5000/api/orders', {
        pid: newOrder.pid,
        sid: newOrder.sid,
        uid: user.uid,
        quantity: parseInt(newOrder.quantity)
      });

      setOpenDialog(false);
      setError('');
      fetchOrders();
      setNewOrder({ pid: '', sid: '', quantity: '' });
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelOrder = async (orderId) => {
    try {
      setCancellingOrder(orderId);
      const response = await axios.post(`http://localhost:5000/api/orders/cancel/${orderId}`, {
        uid: user.uid
      });
      fetchOrders(); // Refresh orders list
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to cancel order');
    } finally {
      setCancellingOrder(null);
    }
  };

  // Add a helper function to format price in Rupees
  const formatPrice = (price) => {
    return `₹${Number(price).toLocaleString('en-IN', {
      maximumFractionDigits: 2,
      minimumFractionDigits: 2
    })}`;
  };

  return (
    <Container>
      <Box mt={4}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <Typography variant="h4">
            Orders
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setError('');
              setOpenDialog(true);
              setNewOrder({ pid: '', sid: '', quantity: '' });
              setSelectedProductSellers([]);
            }}
          >
            Place New Order
          </Button>
        </Box>

        {error && (
          <Paper style={{ padding: '10px', marginBottom: '20px', backgroundColor: '#ffebee' }}>
            <Typography color="error">{error}</Typography>
          </Paper>
        )}

        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Order ID</TableCell>
                <TableCell>Product Name</TableCell>
                <TableCell>Seller</TableCell>
                <TableCell>Quantity</TableCell>
                <TableCell>Price (INR)</TableCell>
                <TableCell>Order Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {orders.map((order) => (
                <TableRow key={order.oid}>
                  <TableCell>{order.oid}</TableCell>
                  <TableCell>{order.pname || order.pid}</TableCell>
                  <TableCell>{order.sname || order.sid}</TableCell>
                  <TableCell>{order.quantity}</TableCell>
                  <TableCell>{formatPrice(order.price/100)}</TableCell>
                  <TableCell>{new Date(order.orderdatetime).toLocaleString()}</TableCell>
                  <TableCell>{order.status || 'active'}</TableCell>
                  <TableCell>
                    {(!order.status || order.status !== 'cancelled') && (
                      <Button
                        variant="outlined"
                        color="error"
                        size="small"
                        onClick={() => handleCancelOrder(order.oid)}
                        disabled={cancellingOrder === order.oid}
                      >
                        {cancellingOrder === order.oid ? (
                          <CircularProgress size={20} />
                        ) : (
                          'Cancel'
                        )}
                      </Button>
                    )}
                  </TableCell>
                </TableRow>
              ))}
              {orders.length === 0 && (
                <TableRow>
                  <TableCell colSpan={8} align="center">
                    No orders found
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>

        <Dialog 
          open={openDialog} 
          onClose={() => {
            setError('');
            setOpenDialog(false);
          }}
        >
          <DialogTitle>Place New Order</DialogTitle>
          <DialogContent>
            {error && (
              <Typography color="error" style={{ marginBottom: '10px' }}>
                {error}
              </Typography>
            )}
            <FormControl fullWidth margin="dense">
              <InputLabel>Select Medicine</InputLabel>
              <Select
                value={newOrder.pid}
                onChange={handleProductChange}
              >
                {products.map((product) => (
                  <MenuItem key={product.pid} value={product.pid}>
                    {product.pname} - {formatPrice(product.price/100)}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            {newOrder.pid && (
              <FormControl fullWidth margin="dense">
                <InputLabel>Select Seller</InputLabel>
                <Select
                  value={newOrder.sid}
                  onChange={(e) => {
                    setError('');
                    setNewOrder({ ...newOrder, sid: e.target.value, quantity: '' });
                  }}
                >
                  {selectedProductSellers.map((seller) => (
                    <MenuItem key={seller.sid} value={seller.sid}>
                      {seller.sname}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            )}

            {newOrder.sid && (
              <TextField
                margin="dense"
                label="Quantity"
                type="number"
                fullWidth
                value={newOrder.quantity}
                onChange={(e) => {
                  setError('');
                  setNewOrder({ ...newOrder, quantity: e.target.value });
                }}
                inputProps={{ min: 1 }}
              />
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => {
              setError('');
              setOpenDialog(false);
            }} color="primary">
              Cancel
            </Button>
            <Button 
              onClick={handlePlaceOrder} 
              color="primary" 
              variant="contained"
              disabled={loading || !newOrder.pid || !newOrder.sid || !newOrder.quantity}
            >
              {loading ? <CircularProgress size={24} /> : 'Place Order'}
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default Orders;
