import React, { useState, useEffect } from 'react';
import {
  Container,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Box,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  CircularProgress,
  Chip,
  Select,
  MenuItem,
  FormControl,
  InputLabel
} from '@mui/material';
import axios from 'axios';

function Orders({ user }) {
  const [orders, setOrders] = useState([]);
  const [error, setError] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [newOrder, setNewOrder] = useState({
    pid: '',
    sid: '',
    quantity: '',
  });
  const [sellers, setSellers] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchOrders();
    fetchSellers();
    fetchProducts();
  }, [user]);

  const fetchOrders = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/orders/${user.uid}`);
      setOrders(response.data);
    } catch (err) {
      console.error('Error fetching orders:', err);
      setError(err.response?.data?.error || 'Failed to fetch orders');
    }
  };

  const fetchSellers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/sellers');
      setSellers(response.data);
    } catch (err) {
      console.error('Error fetching sellers:', err);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products');
      setProducts(response.data);
    } catch (err) {
      console.error('Error fetching products:', err);
    }
  };

  const handlePlaceOrder = async () => {
    try {
      setLoading(true);
      // Validate inputs
      if (!newOrder.pid || !newOrder.sid || !newOrder.quantity) {
        setError('Please fill in all fields');
        return;
      }

      if (parseInt(newOrder.quantity) <= 0) {
        setError('Quantity must be greater than 0');
        return;
      }

      const response = await axios.post('http://localhost:5000/api/orders', {
        pid: newOrder.pid,
        sid: newOrder.sid,
        uid: user.uid,
        quantity: parseInt(newOrder.quantity)
      });

      setOpenDialog(false);
      setError('');
      fetchOrders();
      setNewOrder({ pid: '', sid: '', quantity: '' });
    } catch (err) {
      console.error('Error placing order:', err);
      setError(err.response?.data?.error || 'Failed to place order');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Box mt={4}>
        <Box display="flex" justifyContent="space-between" alignItems="center" mb={3}>
          <Typography variant="h4">
            Orders
          </Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              setError('');
              setOpenDialog(true);
            }}
          >
            Place New Order
          </Button>
        </Box>

        {error && (
          <Paper style={{ padding: '10px', marginBottom: '20px', backgroundColor: '#ffebee' }}>
            <Typography color="error">{error}</Typography>
          </Paper>
        )}

        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Order ID</TableCell>
                <TableCell>Product Name</TableCell>
                <TableCell>Seller</TableCell>
                <TableCell>Quantity</TableCell>
                <TableCell>Price</TableCell>
                <TableCell>Order Date</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {orders.map((order) => (
                <TableRow key={order.oid}>
                  <TableCell>{order.oid}</TableCell>
                  <TableCell>{order.pname || order.pid}</TableCell>
                  <TableCell>{order.sname || order.sid}</TableCell>
                  <TableCell>{order.quantity}</TableCell>
                  <TableCell>₹{(order.price/100).toFixed(2)}</TableCell>
                  <TableCell>{new Date(order.orderdatetime).toLocaleString()}</TableCell>
                </TableRow>
              ))}
              {orders.length === 0 && (
                <TableRow>
                  <TableCell colSpan={6} align="center">
                    No orders found
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>

        <Dialog 
          open={openDialog} 
          onClose={() => {
            setError('');
            setOpenDialog(false);
          }}
        >
          <DialogTitle>Place New Order</DialogTitle>
          <DialogContent>
            {error && (
              <Typography color="error" style={{ marginBottom: '10px' }}>
                {error}
              </Typography>
            )}
            <FormControl fullWidth margin="dense">
              <InputLabel>Product</InputLabel>
              <Select
                value={newOrder.pid}
                onChange={(e) => {
                  setError('');
                  setNewOrder({ ...newOrder, pid: e.target.value });
                }}
              >
                {products.map((product) => (
                  <MenuItem key={product.pid} value={product.pid}>
                    {product.pname} - {product.pid} (₹{(product.price/100).toFixed(2)})
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <FormControl fullWidth margin="dense">
              <InputLabel>Seller</InputLabel>
              <Select
                value={newOrder.sid}
                onChange={(e) => {
                  setError('');
                  setNewOrder({ ...newOrder, sid: e.target.value });
                }}
              >
                {sellers.map((seller) => (
                  <MenuItem key={seller.sid} value={seller.sid}>
                    {seller.sname}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              margin="dense"
              label="Quantity"
              type="number"
              fullWidth
              value={newOrder.quantity}
              onChange={(e) => {
                setError('');
                setNewOrder({ ...newOrder, quantity: e.target.value });
              }}
              inputProps={{ min: 1 }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => {
              setError('');
              setOpenDialog(false);
            }} color="primary">
              Cancel
            </Button>
            <Button 
              onClick={handlePlaceOrder} 
              color="primary" 
              variant="contained"
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Place Order'}
            </Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default Orders;
