import React, { useState } from 'react';
import { TextField, Button, Paper, Typography, Container, Box, Select, MenuItem } from '@material-ui/core';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

function Login({ onLogin }) {
  const [credentials, setCredentials] = useState({ 
    uid: '', 
    pass: '',
    accountType: ''
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', credentials);
      if (response.data.success) {
        onLogin(response.data.user);
        navigate('/dashboard');
      }
    } catch (err) {
      setError('Invalid credentials');
    }
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        backgroundColor: '#e3f2fd',
      }}
    >
      <Container component="main" maxWidth="xs">
        <Paper elevation={3} style={{ padding: '20px', backgroundColor: 'rgba(255, 255, 255, 0.9)' }}>
          <Typography component="h1" variant="h5" align="center" gutterBottom>
            Drug Store Management
          </Typography>
          <form onSubmit={handleSubmit}>
            <Select
              variant="outlined"
              margin="normal"
              required
              fullWidth
              value={credentials.accountType}
              onChange={(e) => setCredentials({ ...credentials, accountType: e.target.value })}
              displayEmpty
            >
              <MenuItem value="" disabled>Select Account Type</MenuItem>
              <MenuItem value="customer">Customer</MenuItem>
              <MenuItem value="vendor">Vendor</MenuItem>
            </Select>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              label="User ID"
              value={credentials.uid}
              onChange={(e) => setCredentials({ ...credentials, uid: e.target.value })}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              label="Password"
              type="password"
              value={credentials.pass}
              onChange={(e) => setCredentials({ ...credentials, pass: e.target.value })}
            />
            {error && (
              <Typography color="error" align="center">
                {error}
              </Typography>
            )}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              style={{ marginTop: '16px' }}
            >
              Login
            </Button>
            <Box mt={2} textAlign="center">
              <Link to="/register" style={{ textDecoration: 'none' }}>
                <Typography color="primary">
                  Don't have an account? Register
                </Typography>
              </Link>
            </Box>
          </form>
        </Paper>
      </Container>
    </Box>
  );
}

export default Login;
